import React from 'react'
import './loginpage.css'
import { useState } from 'react';
import { Link } from 'react-router-dom';

const Loginpage = (props) => {
  const [username, setUsername] = useState('');
  const [password,setPassword] = useState('');

    const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };
  if (!password || !username) {
    alert('Please fill in all mandatory fields.');
    return;
  }

    return (
      <section className='container login-container'>
        <div className='left'>
          <h1>Welcome Back! </h1>
          <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Hic, reiciendis! Deleniti blanditiis doloribus unde beatae dolorum sunt atque ipsa, ut non perspiciatis cupiditate, soluta eum dolorem veritatis quisquam dignissimos debitis! Velit cupiditate quaerat eum sint.</p>
        </div>

        <div className='right'>
        <h2>Sign In</h2>
        <form>
          <div className="form-group">
            <label htmlFor="exampleInputUsername">Username</label>
            <input value={username} onChange={handleUsernameChange} type="username" className="form-control" id="exampleInputUsername" aria-describedby="UsernameHelp" placeholder="Enter Username" />
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputPassword1">Password</label>
            <input value={password} onChange={handlePasswordChange}type="password" className="form-control" id="exampleInputPassword1" placeholder="Password" />
          </div>
          <a id='forgot' href='#'>Forgot Password</a>
          <Link to = {`/LandingPage/${username}/`}>
          <button type="submit" className="btn btn-primary">Submit</button>
          </Link>
        </form>
        </div>
      </section>
    )  
}

export default Loginpage
